def process(value:Double, preprocess:(Double) => Double) = {
    val tmp = preprocess(value)
    2*tmp
}

process(12, (x:Double) => x-1)

def compute(xs: Int) = {
  def twice(y: Int) = 2*y
  def inc(y: Int) = y+1
  twice(xs) + inc(xs)
}

println(compute(23))

