def fn(name:String) = {
    name match {
        case "Alice" => println("Hello Alice")
        case "Bob" => println("Hi Bob")
        case x:String if x.length > 10 => println("Someone else (" + x + ") with a long name")
        case x:String => println("Someone else (" + x + ")")
    }
}

