def foreach(xs: List[Int], fn:(Int)=>Unit) : Unit = {
  xs match {
      case head :: tail =>
          fn(head)
          foreach(tail, fn)
      case List() => Unit
  }    
}

println(foreach(List(1, 9, 2, 8, 3, 7, 4), println))

def reduce(agg:(Int,Int), value:Int) = (agg._1 + value, agg._2 + 1)
def combine(left:(Int,Int),right:(Int,Int)) = (left._1 + right._1 , left._2 + right._2)
val zero = (0,0)

val agg = range.aggregate(zero)(reduce, combine)
val avg = agg._1.toFloat / agg._2.toFloat

