// Define a new case class for result type. We also could have used a tuple instead
case class AverageAge(
    sex:String,
    average:Double
)

// Define a simple Scala averaging method for persons age
def avg_age(persons:Iterator[Person]) = {
    val (age,count) = persons
        .map(p => (p.age,1))
        .reduce((l,r) => (l._1 + r._1, l._2 + r._2))

    age.toFloat / count.toFloat
}

// Perform aggregation
val result = persons
    .groupByKey(_.sex)
    .mapGroups { case (sex,values) => AverageAge(sex, avg_age(values)) }

