val zero = (0,0)
def reduce(state:(Int,Int), value:Int) : (Int,Int) = {
    val (count,sum) = state
    (count + 1, sum + value)
}
def combine(left:(Int,Int), right:(Int,Int)) : (Int,Int) = {
    (left._1 + right._1, left._2 + right._2)
}

val result = numbers.aggregate(zero)(reduce, combine)
val avg = result._2.toFloat / result._1

val zero = Map[String,Int]()
def reduce(map:Map[String,Int], word:String) : Map[String,Int] = map.updated(word, map.getOrElse(word, 0) + 1)
def combine(left:Map[String,Int], right:Map[String,Int]) : Map[String,Int] = {
    val words = left.keySet ++ right.keySet
    words.map(word => (word,left.getOrElse(word, 0) + right.getOrElse(word, 0))).toMap
}

val counts = words.aggregate(zero)(reduce, combine)

val zero = (0.0, 0)
def reducer(acc:(Double,Int), value:Double) = (acc._1 + value, acc._2 + 1)
def combiner(left:(Double,Int), right:(Double,Int)) = (left._1 + right._1, left._2 + right._2)

val rdd = sc.parallelize(Array(("a",1.0), ("b",3.0), ("c", 17.0), ("b", 23.0), ("c",12.0)))
val result = rdd.aggregateByKey(zero)(reducer, combiner).mapValues(kv => kv._1/kv._2)

println(result.collect().mkString(","))
