import org.apache.spark.ml.evaluation.Evaluator

class AccuracyClassificationEvaluator(override val uid: String) extends org.apache.spark.ml.evaluation.Evaluator {

    def this() = this(org.apache.spark.ml.util.Identifiable.randomUID("accuEval"))

    val labelCol: org.apache.spark.ml.param.Param[String] = new org.apache.spark.ml.param.Param[String](this, "labelCol", "label column name")
    setDefault(labelCol, "label")
    def getLabelCol: String = $(labelCol)
    def setLabelCol(value:String): this.type = set(labelCol, value)

    val predictionCol: org.apache.spark.ml.param.Param[String] = new org.apache.spark.ml.param.Param[String](this, "predictionCol", "prediction column name")
    setDefault(predictionCol, "prediction")
    def getPredictionCol: String = $(predictionCol)
    def setPredictionCol(value:String): this.type = set(predictionCol, value)

    override def evaluate(dataset: org.apache.spark.sql.Dataset[_]) : Double = {
        val num_total = dataset.count()
        val num_correct = dataset.filter(dataset(getLabelCol) === dataset(getPredictionCol)).count()
        num_correct.toDouble / num_total
    }

    override def copy(extra: org.apache.spark.ml.param.ParamMap): AccuracyClassificationEvaluator = null // defaultCopy(extra)

}

