class PunctuationCleanupTransformer(override val uid: String) extends org.apache.spark.ml.Transformer {
    def this() = this(org.apache.spark.ml.util.Identifiable.randomUID("accuEval"))

    val inputCol: org.apache.spark.ml.param.Param[String] = new org.apache.spark.ml.param.Param[String](this, "inputCol", "input column name")
    setDefault(inputCol, "input")
    def getInputCol: String = $(inputCol)
    def setInputCol(value:String): this.type = set(inputCol, value)

    val outputCol: org.apache.spark.ml.param.Param[String] = new org.apache.spark.ml.param.Param[String](this, "outputCol", "output column name")
    setDefault(outputCol, "output")
    def getOutputCol: String = $(outputCol)
    def setOutputCol(value:String): this.type = set(outputCol, value)

    override def transform(dataset:org.apache.spark.sql.Dataset[_]) = {
        val removePunctuation = org.apache.spark.sql.functions.udf { text:String => text.replaceAll("\\p{Punct}", " ") }
        dataset.withColumn(getOutputCol, removePunctuation(dataset(getInputCol)))
    }

    override def transformSchema(schema:org.apache.spark.sql.types.StructType) = {
        val outputColName = $(outputCol)
        val outCol = org.apache.spark.sql.types.StructField(outputColName, org.apache.spark.sql.types.StringType)

        if (schema.fieldNames.contains(outputColName)) {
          throw new IllegalArgumentException(s"Output column $outputColName already exists.")
        }
        org.apache.spark.sql.types.StructType(schema.fields :+ outCol)
    }

    override def copy(extra: org.apache.spark.ml.param.ParamMap): PunctuationCleanupTransformer = defaultCopy(extra)
}

