def reduce(agg:(Int,Int), value:Int) = (agg._1 + value, agg._2 + 1)
def combine(left:(Int,Int),right:(Int,Int)) = (left._1 + right._1 , left._2 + right._2)
val zero = (0,0)

val agg = range.aggregate(zero)(reduce, combine)
val avg = agg._1.toFloat / agg._2.toFloat

val agg = range.aggregate((0,0))(
    { (agg,value) => (agg._1 + value, agg._2 + 1) },
    { (left,right) => (left._1 + right._1 , left._2 + right._2)}
)
val avg = agg._1.toFloat / agg._2.toFloat

def half(i:Int) = i match { 
    case i:Int if i%2 == 0 => Some(i/2) 
    case _ => None 
}

def pfn(i:Option[Int]) = i match {
    case i:Some[Int] => println("Got a value!")
    case None => println("Got nothing")
}
