val zero = (0.0, 0)
def reducer(acc:(Double,Int), value:Double) = (acc._1 + value, acc._2 + 1)
def combiner(left:(Double,Int), right:(Double,Int)) = (left._1 + right._1, left._2 + right._2)

val rdd = sc.parallelize(1 to 10).map(_.toDouble)
val result = rdd.aggregate(zero)(reducer, combiner)

println(result._1/result._2)

val zero = (0.0, 0)
def reducer(acc:(Double,Int), value:Double) = (acc._1 + value, acc._2 + 1)
def combiner(left:(Double,Int), right:(Double,Int)) = (left._1 + right._1, left._2 + right._2)

val rdd = sc.parallelize(Array(("a",1.0), ("b",3.0), ("c", 17.0), ("b", 23.0), ("c",12.0)))
val result = rdd.aggregateByKey(zero)(reducer, combiner).mapValues(kv => kv._1/kv._2)

println(result.collect().mkString(","))
